import React from "react";
import { useForm } from 'react-hook-form';
import { loginForm } from "../Services/login";

import { Container, Col, Row } from "react-bootstrap"

const Home = () => {

  const { register, handleSubmit, errors } = useForm()

  const onSubmit = (data) => {
    const token = loginForm(data)
      .then((response) => {
        return response.data.token;
      })
      .catch((error) => {
        alert(error);
      })
    localStorage.setItem('token', token);
  }

  return (
    <Container>
      <Col>
        2 of 4
        <form onSubmit={handleSubmit(onSubmit)}>
          <Row>
            <label>Email</label>
            <input ref={register({ required: true })} name="login" type="text"></input>
          </Row>

          <Row>
            <label>Senha</label>
            <input ref={register({ required: true })} name="pass" type="password"></input>
          </Row>

          <div>
            {errors.password && "*Senha inv치lida"}
            {errors.email && "*Email obrigat칩rio"}
          </div>

          <button>Login</button>
        </form>
      </Col>
    </Container>
  );
};

export default Home;
