import React from "react";
import { useForm } from 'react-hook-form';
import { loginForm } from "../Services/login";

import { Container, Col, Row, Form } from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {

  const { register, handleSubmit, errors } = useForm()

  const onSubmit = (data) => {
    const token = loginForm(data)
      .then((response) => {
        return response.data.token;
      })
      .catch((error) => {
        alert(error);
      })
    localStorage.setItem('token', token);
  }

  return (
    <Container>
      <h2 style={{textAlign: "center"}}>Login</h2>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col></Col>
          <Form.label>Email</Form.label>
          <input ref={register({ required: true })} name="login" type="text"></input>
          <Col></Col>
        </Row>

        <Row>
          <Col></Col>
          <label>Senha</label>
          <input ref={register({ required: true })} name="pass" type="password"></input>
          <Col></Col>
        </Row>

        <div>
          {errors.password && "*Senha inv치lida"}
          {errors.email && "*Email obrigat칩rio"}
        </div>

        <button style={{marginLeft: "50%"}}>Login</button>
      </Form>
    </Container>
  );
};

export default Home;
