import React from "react";
import { useForm } from 'react-hook-form';
import { loginForm } from "../Services/login";

import { Container, Col, Row } from "react-bootstrap"

const Home = () => {

  const { register, handleSubmit, errors } = useForm()

  const onSubmit = (data) => {
    const token = loginForm(data)
      .then((response) => {
        return response.data.token;
      })
      .catch((error) => {
        alert(error);
      })
    localStorage.setItem('token', token);
  }

  return (
    <Container>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Row>
          <Col xs={5}><label>Email</label></Col>

          <input ref={register({ required: true })} name="login" type="text"></input>
        </Row>

        <Row>
          <label>Senha</label>
          <input ref={register({ required: true })} name="pass" type="password"></input>
        </Row>

        <div>
          {errors.password && "*Senha inv치lida"}
          {errors.email && "*Email obrigat칩rio"}
        </div>

        <button>Login</button>
      </form>
    </Container>
  );
};

export default Home;
